import { addToCart } from "../actions/cartActions";
import { CART_ADD_ITEM, CART_REMOVE_ITEM } from "../constants/cartConstants";

export const cartReducer = (state: {}, action) => {
  switch (action.type) {
    case CART_ADD_ITEM:
      return { cartItems: action.payload };
    case CART_REMOVE_ITEM:
      return { cartItems: action.payload };
    default:
      return state;
  }
};
